func PrintAsciArt(asciart AsciArt) (err error) {

	asciarray := make([]string, 0)

	asci := asciart.asci
	lensep := asciart.lensep

	if len(asci)%lensep == 0 {

		for i := 0; i < (len(asci) / lensep); i++ {

			prefix := sequences.Prefix(asci, lensep)

			asciarray = append(asciarray, prefix)

			asci = asci[(len(asci) - lensep):]
		}
		fmt.Println(asci)
		for _, line := range asciarray {
			fmt.Println(line)
			//time.Sleep(5 * time.Second)
		}
	} else {
		err = fmt.Errorf("check line length of asci art")
	}
	return
}

func PrintNyan() {
	PrintAsciArt(Nyancat)
}
